import React from 'react';
export declare const CONTROLCLASS = "wpeform-component-ripple";
declare type rippleState = {
    key: number;
    width: number;
    height: number;
    left: number;
    top: number;
    onRest: () => void;
    darkBg?: boolean;
};
/**
 * A hook to create ripple with maximum possible customization for any kinds
 * of elements.
 *
 * This is not a component, rather a hook which lets you attach ripple to any
 * kinds of other component. The only condition is, the parent component should
 * have the following CSS.
 *
 * ```css
 * position: relative;
 * overflow: hidden;
 * ```
 *
 * Use it like
 *
 * ```jsx
 * function Component() {
 * 	const [addRipple, ripples] = useRipple();
 * 	return (
 * 		<button style={{position: 'relative', overflow: 'hidden'}}>
 * 			Ripple Button
 * 		</button>
 *  );
 * }
 * ```
 */
export default function useRipple({ darkBg }?: Pick<rippleState, 'darkBg'>): readonly [(event: React.MouseEvent<HTMLElement, any>) => void, JSX.Element[]];
export {};
