export declare type StoreType<T> = {
    [key: string]: T;
};
/**
 * A General Store class to store variables of same type in a hashmap.
 *
 * Say we want to store different Form elements which would be used in various
 * parts of the app. For that, we would do something like
 *
 * ```js
 * const formElements = new GeneralStore<FormElement>();
 * formElements.add('checkbox', checkbox).add('radio', radio);
 * ```
 */
export default class GeneralStore<T = any> {
    private store;
    constructor(initialStore?: StoreType<T>);
    add(key: string, value: T): GeneralStore;
    remove(key: string): GeneralStore;
    get(key: string): T;
    getAll(): StoreType<T>;
}
