/**
 * Unique string Id generator class.
 *
 * Adopted from
 * @link https://stackoverflow.com/a/12504061/2754557
 *
 * @usage
 * const ids = new StringIdGenerator();
 *
 *	ids.next(); // 'a'
 *	ids.next(); // 'b'
 *	ids.next(); // 'c'
 *
 *	// ...
 *	ids.next(); // 'z'
 *	ids.next(); // 'A'
 *	ids.next(); // 'B'
 *
 *	// ...
 *	ids.next(); // 'Z'
 *	ids.next(); // 'aa'
 *	ids.next(); // 'ab'
 *	ids.next(); // 'ac'
 */
export default class StringIdGenerator {
    private chars;
    private nextId;
    constructor(chars?: string);
    next(): string;
    private increment;
    [Symbol.iterator](): Generator<string, void, unknown>;
}
